import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { getToken } from '../utils/auth';
import AdminProductForm from '../components/AdminProductForm';

export default function AdminProductsPage(){
  const [products, setProducts] = useState([]);
  const [editing, setEditing] = useState(null);

  const load = async () => {
    try {
      const token = getToken();
      const headers = token ? { Authorization: `Bearer ${token}` } : {};
      const res = await axios.get('/api/products', { headers });
      setProducts(res.data);
    } catch (err) {
      console.error('Error loading products:', err);
    }
  };

  useEffect(() => { load(); }, []);

  const del = async (id) => {
    if (!window.confirm('Delete product?')) return;
    try {
      const token = getToken();
      const headers = token ? { Authorization: `Bearer ${token}` } : {};
      await axios.delete(`/api/products/${id}`, { headers });
      load();
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to delete product');
    }
  };

  return (
    <div className="container page">
      <h1>Manage Products</h1>
      <AdminProductForm editProduct={editing} onSaved={() => { setEditing(null); load(); }} />

      <ul>
        {products.map(p => (
          <li key={p._id}>
            <strong>{p.name}</strong> — ₹{p.price} — {p.countInStock} in stock
            <button onClick={() => setEditing(p)}>Edit</button>
            <button onClick={() => del(p._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
