import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function AdminProductForm({ editProduct, onSaved }) {
  const [form, setForm] = useState({ name:'', description:'', price:'', countInStock:'' });

  useEffect(() => {
    if (editProduct) setForm(editProduct);
  }, [editProduct]);

  const handle = e => setForm({...form, [e.target.name]: e.target.value});

  const submit = async e => {
    e.preventDefault();
    try {
      if (editProduct && editProduct._id) {
        await axios.put(`/api/products/${editProduct._id}`, form);
      } else {
        await axios.post('/api/products', form);
      }
      onSaved && onSaved();
      setForm({ name:'', description:'', price:'', countInStock:''});
    } catch (err) {
      console.error(err);
      alert('Failed to save product');
    }
  };

  return (
    <form onSubmit={submit} style={{ marginBottom:20 }}>
      <input name="name" value={form.name} onChange={handle} placeholder="Name" required />
      <input name="price" value={form.price} onChange={handle} placeholder="Price" required />
      <input name="countInStock" value={form.countInStock} onChange={handle} placeholder="Stock" />
      <textarea name="description" value={form.description} onChange={handle} placeholder="Description" />
      <button type="submit">Save</button>
    </form>
  );
}
