import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function CheckoutPage(){
  const [cart, setCart] = useState([]); // for demo, user will paste product ids+qty
  const [shipping, setShipping] = useState({ address:'', city:'', postalCode:'', country:'' });

  const placeOrder = async () => {
    if (cart.length === 0) return alert('Cart empty. Add items manually for demo.');
    const orderItems = cart.map(i => ({ product: i.productId, name: i.name, price: i.price, qty: i.qty }));
    const res = await axios.post('/api/orders', { orderItems, shippingAddress: shipping, paymentMethod: 'COD' });
    alert('Order placed: ' + res.data._id);
  };

  return (
    <div className="container page">
      <h1>Checkout (Demo)</h1>
      <p>For the lab demo, manually add cart items below:</p>

      <textarea placeholder='Paste JSON like [{"productId":"id","name":"Demo","price":499,"qty":2}]' onBlur={e=>{
        try { setCart(JSON.parse(e.target.value)); } catch (err) { console.error(err); alert('invalid JSON'); }
      }} style={{width:'100%',height:120}} />

      <h3>Shipping</h3>
      <input placeholder="Address" value={shipping.address} onChange={e=>setShipping({...shipping,address:e.target.value})} />
      <input placeholder="City" value={shipping.city} onChange={e=>setShipping({...shipping,city:e.target.value})} />
      <input placeholder="Postal code" value={shipping.postalCode} onChange={e=>setShipping({...shipping,postalCode:e.target.value})} />
      <input placeholder="Country" value={shipping.country} onChange={e=>setShipping({...shipping,country:e.target.value})} />

      <button onClick={placeOrder}>Place Order</button>
    </div>
  );
}
