import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { getToken } from '../utils/auth';
import AdminProductForm from '../components/AdminProductForm';

export default function VendorDashboard() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(null);

  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    try {
      const token = getToken();
      const res = await axios.get('/api/products/vendor/my', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setProducts(res.data);
    } catch (err) {
      console.error('Error loading products:', err);
      alert('Failed to load products');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Delete this product?')) return;
    try {
      const token = getToken();
      await axios.delete(`/api/products/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      loadProducts();
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to delete product');
    }
  };

  if (loading) {
    return <div className="container page">Loading...</div>;
  }

  return (
    <div className="container page">
      <h1>Vendor Dashboard</h1>
      <p>Manage your products</p>

      <AdminProductForm 
        editProduct={editing} 
        onSaved={() => { 
          setEditing(null); 
          loadProducts(); 
        }} 
      />

      <div className="vendor-products">
        <h2>My Products ({products.length})</h2>
        {products.length === 0 ? (
          <p>No products yet. Create your first product above.</p>
        ) : (
          <div className="products-grid">
            {products.map(product => (
              <div key={product._id} className="product-card">
                {product.image && (
                  <img src={product.image} alt={product.name} className="product-image" />
                )}
                <div className="product-info">
                  <h3>{product.name}</h3>
                  <p>â‚¹{product.price.toLocaleString()}</p>
                  <p>Stock: {product.countInStock}</p>
                  <div className="product-actions">
                    <button onClick={() => setEditing(product)} className="btn">Edit</button>
                    <button onClick={() => handleDelete(product._id)} className="btn-danger">Delete</button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
