// frontend/src/pages/CheckoutPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { loadCart, saveCart, clearCart } from '../utils/cart';
import { useNavigate } from 'react-router-dom';

export default function CheckoutPage(){
  const [cart, setCart] = useState(loadCart());
  const [shipping, setShipping] = useState({ address:'', city:'', postalCode:'', country:'' });
  const [coupon, setCoupon] = useState('');
  const [offerResult, setOfferResult] = useState(null);
  const navigate = useNavigate();

  useEffect(()=> setCart(loadCart()), []);

  const itemsPrice = cart.reduce((s, it) => s + (it.price * it.qty), 0);
  const tax = +(itemsPrice * 0.05).toFixed(2);
  const shippingPrice = itemsPrice > 1000 ? 0 : 50;
  const subtotal = +(itemsPrice + tax + shippingPrice).toFixed(2);
  const totalAfterCoupon = offerResult ? offerResult.newTotal + tax + shippingPrice : subtotal;

  async function applyCoupon() {
    if (!coupon) return alert('Enter coupon code');
    try {
      const res = await axios.post('/api/offers/apply', { code: coupon, itemsPrice });
      setOfferResult(res.data);
      alert(`Coupon applied: ${res.data.offer.code} — discount ₹${res.data.discount}`);
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to apply coupon');
      setOfferResult(null);
    }
  }

  async function placeOrder() {
    if (cart.length === 0) return alert('Cart is empty');
    const orderItems = cart.map(i => ({ product: i.productId, name: i.name, price: i.price, qty: i.qty }));
    const itemsPrice = cart.reduce((s, it) => s + (it.price * it.qty), 0);
    const discounts = offerResult ? offerResult.discount : 0;
    const finalItemsPrice = +(itemsPrice - discounts).toFixed(2);
    const taxPrice = +(finalItemsPrice * 0.05).toFixed(2);
    const shippingPrice = finalItemsPrice > 1000 ? 0 : 50;
    const totalPrice = +(finalItemsPrice + taxPrice + shippingPrice).toFixed(2);

    try {
      const payload = { orderItems, shippingAddress: shipping, paymentMethod: 'COD', itemsPrice: finalItemsPrice, taxPrice, shippingPrice, totalPrice };
      const res = await axios.post('/api/orders', payload);
      clearCart();
      alert('Order placed: ' + res.data._id);
      navigate('/');
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to place order');
    }
  }

  function updateQty(idx, delta) {
    const newCart = [...cart];
    newCart[idx].qty = Math.max(1, newCart[idx].qty + delta);
    saveCart(newCart);
    setCart(newCart);
  }

  function removeItem(idx) {
    const newCart = [...cart];
    newCart.splice(idx,1);
    saveCart(newCart);
    setCart(newCart);
  }

  return (
    <div className="container page">
      <h1>Checkout</h1>
      {cart.length === 0 ? <p>Your cart is empty.</p> : (
        <div>
          <h3>Items</h3>
          <ul>
            {cart.map((it, idx) => (
              <li key={it.productId} style={{marginBottom:12}}>
                <div style={{display:'flex', justifyContent:'space-between', alignItems:'center'}}>
                  <div>
                    <strong>{it.name}</strong>
                    <div>₹{it.price} × {it.qty} = ₹{it.price * it.qty}</div>
                  </div>
                  <div>
                    <button onClick={() => updateQty(idx, -1)}>-</button>
                    <button onClick={() => updateQty(idx, +1)}>+</button>
                    <button onClick={() => removeItem(idx)}>Remove</button>
                  </div>
                </div>
              </li>
            ))}
          </ul>

          <h3>Apply Coupon</h3>
          <input placeholder="CODE (e.g. WELCOME10)" value={coupon} onChange={e=>setCoupon(e.target.value)} />
          <button onClick={applyCoupon}>Apply</button>
          {offerResult && <div>Applied: {offerResult.offer.code} — {offerResult.offer.discountPercent}% off — discount ₹{offerResult.discount}</div>}

          <h3>Shipping</h3>
          <input placeholder="Address" value={shipping.address} onChange={e=>setShipping({...shipping,address:e.target.value})} />
          <input placeholder="City" value={shipping.city} onChange={e=>setShipping({...shipping,city:e.target.value})} />
          <input placeholder="Postal code" value={shipping.postalCode} onChange={e=>setShipping({...shipping,postalCode:e.target.value})} />
          <input placeholder="Country" value={shipping.country} onChange={e=>setShipping({...shipping,country:e.target.value})} />

          <h3>Order Summary</h3>
          <div>Items: ₹{itemsPrice}</div>
          <div>Tax (5%): ₹{tax}</div>
          <div>Shipping: ₹{shippingPrice}</div>
          {offerResult && <div>Coupon discount: ₹{offerResult.discount}</div>}
          <div><strong>Total: ₹{totalAfterCoupon}</strong></div>

          <div style={{marginTop:12}}>
            <button onClick={placeOrder}>Place Order (Cash on Delivery)</button>
          </div>
        </div>
      )}
    </div>
  );
}
